%% Golden Section Method %% res = golden(-0.5, 0.5, 10^-5, 100);disp(res);function y = f(x)    y = -5*x^5 + 4*x^4 - 12*x^3 + 11*x^2 - 2*x + 1;endfunction t = golden(l, h, e, max_iter)    g = ((sqrt(5) - 1)/2);    x1 = l + (1 - g)*(h - l);    x2 = l + g*(h - l);    i = 0;    while abs(h - l) > e && i < max_iter        if f(x1) > f(x2)            l = x1;            x1 = x2;            x2 = l + g*(h - l);        else            h = x2;            x2 = x1;            x1 = l + (1 - g)*(h - l);        end        i = i + 1;    end    disp(i);    t = (l + h)/2;end